import React from 'react';
import Login from '../Login/Login';  // Adjust relative path if needed
import banner from '../../assets/CryptoKnow Banner.webp'; // Replace with your banner image path

function CryptoKnow({ user }) {
  return (
    <div>
      {/* Banner image at the top */}
      <img src={banner} alt="Banner" style={{ width: '100%', objectFit: 'cover' }} />

      {/* Header section with centered title and login/greeting */}
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        padding: '20px 10px',
        textAlign: 'center'
      }}>
        <h1>Welcome to CryptoKnow - Your Hub for Crypto Opportunities!</h1>
        {user ? (
          <p style={{ margin: '10px 0', fontSize: '1.2rem' }}>
            Hello, {user.user_metadata?.full_name || user.email}
          </p>
        ) : (
          <Login />
        )}
      </div>
    </div>
  );
}

export default CryptoKnow;
